using System.Collections;using System.Collections.Generic;using UnityEngine;using UnityEngine.UI;public class MainMenuScript : BaseMenuScript{    private GameObject mainMenu;    [SerializeField] GameObject playerSelectMenu;    [SerializeField] GameObject controlsMenu;    [SerializeField] Button onePlayerButton;    [SerializeField] Button twoPlayerButton;    [SerializeField] Button controlsButton;    [SerializeField] Button quitButton;

    protected override void Init()    {        buttonMap = new Button[,] { {onePlayerButton},                                     {twoPlayerButton},
                                    {controlsButton},                                    {quitButton}        };
        mainMenu = gameObject;        if (GameData.GetSameSettings())        {            GoToPlayerSelect();        }    }    public void GoToMainMenu()    {        mainMenu.SetActive(true);        playerSelectMenu.SetActive(false);        controlsMenu.SetActive(false);    }    public void GoToOnePlayerSelect()    {        GameData.SetPlayers(1);        GoToPlayerSelect();    }    public void GoToTwoPlayerSelect()    {        GameData.SetPlayers(2);        GoToPlayerSelect();    }    public void GoToControls()    {        mainMenu.SetActive(false);        controlsMenu.SetActive(true);    }    private void GoToPlayerSelect()    {        mainMenu.SetActive(false);        playerSelectMenu.SetActive(true);    }    public void Quit()    {        Application.Quit();    }}